import Clock from './Clock';
import { useState, useEffect } from 'react';
import axios from 'axios';
import styled from "styled-components";

export default function Profile(){
    const [user,setUser] = useState('User')
    const [debit, setDebit] = useState([])
    const [credit,setCredit] = useState([])
    const [specifyData, setSpeciftData] = useState('debit')

    async function getDebits(){
        const debits = await axios.get('https://moj-api.herokuapp.com/debits')
        setDebit(debits.data) 
    }

    async function getCredits(){
        const credits = await axios.get('https://moj-api.herokuapp.com/credits')
        setCredit(credits.data)
    }

    useEffect( () => {
      getDebits();
      getCredits();
      }, []);

    const debitList = debit.map(d =>
    <li key={d.id}> 
    <b>Description: </b>{d.description},<b>Price: </b>{d.amount}, <b>Date:</b> {d.date.slice(0,10)}
     </li>
    ) 

    const creditList = credit.map(c =>
        <li key={c.id}> 
        <b>Description: </b>{c.description},<b>Price: </b>{c.amount}, <b>Date:</b> {c.date.slice(0,10)}
         </li>
        ) 
        const types = ["Debit", "Credit"];
        const Button = styled.button`
  background-color: ${(props) => theme[props.theme].default};
  color: white;
  padding: 5px 15px;
  border-radius: 5px;
  outline: 0;
  text-transform: uppercase;
  margin: 10px 0px;
  cursor: pointer;
  box-shadow: 0px 2px 2px lightgray;
  transition: ease background-color 250ms;
  &:hover {
    background-color: ${(props) => theme[props.theme].hover};
  }
  &:disabled {
    cursor: default;
    opacity: 0.7;
  }
`;

Button.defaultProps = {
  theme: "blue"
};
        const ButtonToggle = styled(Button)`
  opacity: 0.7;
  ${({ active }) =>
    active &&
    `
    opacity: 1; 
  `}
`;
        function ToggleGroup() {
          const [active, setActive] = useState(types[0]);
          return (
            <div>
              {types.map((type) => (
                <ButtonToggle active={active === type} onClick={() => setActive(type)}>
                  {type}
                </ButtonToggle>
              ))}
            </div>
          );
        }

    return(
        <>
    <div style={{display:'flex', marginLeft:100}}>
        <h2 style={{marginRight:'10px'}}> Hello {user}! </h2> 
        <Clock/>
  </div>
 <ToggleGroup/>
  <ul>{specifyData ==='debit' ? debitList: creditList}</ul>
    </>
    )
}
