import { useState, useEffect } from 'react';
import {FormControl, TextField, Input} from '@mui/material';
import { Button, Box } from '@mui/material';
export default function Form(props){
    const [userName,setUserName] = useState('')
    const [bckClr,setBckClr] = useState('')
    const [txtClr,setTxtClr] = useState('')



    function handleSubmit(e){
        e.preventDefault();
        console.log(e.target)
        if(e.target.name ==='username')
            props.changeData(e.target.username);
        else if(e.target.name ==='bckclr')
            props.changeData(e.target.bckclr);
        else if(e.target.name==='txtclr')
            props.changeData(e.target.txtclr);
    }

    function handleChange(e){
        e.preventDefault();
        console.log(e.target.name)
        if(e.target.name ==='username')
            setUserName(e.target.value)
        else if(e.target.name ==='bckclr')
            setBckClr(e.target.value)
        else if(e.target.name==='txtclr')
            setTxtClr(e.target.value)
    }

    return(
<form style={{textAlign: 'center'}}  onSubmit={handleSubmit}>

<Input 
  placeholder="Username"
  value={userName}
  name='username'
  onChange={handleChange}
/>
<Button sx={{color:'black',borderColor:'black'}} variant='outlined' type='submit'>
Change
</Button><br/>
<Input 
variant='contained'
  placeholder="Background Color"
  value={bckClr}
  name='bckclr'
  onChange={handleChange}
/><Button sx={{color:'black',borderColor:'black'}} variant='outlined' type='submit'>
Change
</Button>
<br/>
<Input 

  placeholder="Text Color"
  value={txtClr}
  name='txtclr'
  onChange={handleChange}
/>
<Button sx={{color:'black',borderColor:'black'}} variant='outlined' type='submit'>
Change
</Button>
<br/><br/>

</form>
    )
}
