    const [formData,setFormData] = useState({
        name:'UserName',
        bckclr :'hxxx',
        txtclr :'hxx'
    })

    function handleSubmit(e){
        console.log(e.target)
    }

    function handleChange(e){
        e.preventDefault();
        const { name, value } = e.target
        setFormData((prev)=>({...prev,[name]:value}))
        console.log(value)
    }
    return(
    <form style={{textAlign: 'center'}}   onSubmit={props.changeData}>
<TextField type='text'
helperText="Please enter your username"
  id="username"
  label="Username"
  value={formData.name}
  onChange={handleChange}
  onSubmit={e=>props.changeData(e)}
//   onSubit={props.changeName}
/>
<Button onClick={props.changeData}>Change</Button>
</form>
    )
