import Clock from './Clock';
import { useState, useEffect } from 'react';
import axios from 'axios';

export default function Profile(){
    const [user,setUser] = useState('Hello User!')
    const [debit, setDebit] = useState([])
    const [credit,setCredit] = useState([])

    async function getDebits(){
        const debits = await axios.get('https://moj-api.herokuapp.com/debits')
        setDebit(debits.data) 
    }

    async function getCredits(){
        const credits = await axios.get('https://moj-api.herokuapp.com/credits')
        setCredit(credits.data)
    }
    useEffect( () => {
      getDebits();
      getCredits();
      }, []);

    function debitView(){
           const debitList = debit.map(d =>
             <li key={d.id}> <b>Description: </b>{d.description},<b>Price: </b>{d.amount}, <b>Date:</b> {d.date.slice(0,10)} </li>
         )
            return <ul>{debitList}</ul>
};

    return(
        <>
    <div style={{display:'flex', marginLeft:100}}>
        <h2 style={{marginRight:'10px'}}> {user} </h2> 
        <Clock/>
  </div>
  {debitView}
  {/* <ul> 
              {debit.map(d =>{ 
            return <li key={d.id}> <b>Description: </b>{d.description},<b>Price: </b>{d.amount}, <b>Date:</b> {d.date.slice(0,10)} </li>
         })}
         </ul> */}
  <debitView/>

    </>
    )
}
