import Clock from './Clock';
import { useState, useEffect } from 'react';
import axios from 'axios';

export default function Profile(){
    const [user,setUser] = useState('Hello User!')
    const [debit, setDebit] = useState([])
    const [credit,setCredit] = useState([])

    async function getDebits(){
        const debits = await axios.get('https://moj-api.herokuapp.com/debits')
        setDebit(debits.data)
    }

    async function getCredits(){
        const credits = await axios.get('https://moj-api.herokuapp.com/credits')
        setCredit(credits.data)
    }
    useEffect( () => {
      getDebits();
      getCredits();
      }, []);

    function debitView(){
         const debits = debit.map(d =>{
        return <li key={d.id}> {d} </li>

        })
        console.log(debits)
          return <ul>{debits}</ul>
    }

    return(
        <>
    <div style={{display:'flex'}}>
        <h2 onClick={debitView} style={{marginRight:'10px'}}> {user} </h2> 
        <Clock/>
  </div>
  <ul>
      {debit.map(d =>
         <li key={d.id}> {d} </li>)} 

        </ul>
    </>
    )
}
